import React, { useState } from 'react';
import './ModalAddBrand.scss'; // Asumiendo que tienes un archivo SCSS separado para los estilos
import './ModalFindProduct.scss';
import { useEffect } from 'react';
const ModalFindProduct = ({ isOpen, onClose, onAdd, categories, brands, productslist }) => {
  const [brandName, setBrandName] = useState('');
  const [category, setCategory] = useState('');
  const [brand, setBrand] = useState('');
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    if (isOpen) {
      // Sólo establece los productos si el modal está abierto
      setProducts(productslist);
      setFilteredProducts(productslist);
    }
  }, [productslist, isOpen]);

  useEffect(() => {
    // Este efecto no debe ser condicional y debe ejecutarse siempre
    const timer = setTimeout(() => {
      filterProducts();
    }, 500);
    return () => clearTimeout(timer);
  }, [brandName, category, brand, products]); // Reaccionar cuando estos valores cambien

  const filterProducts = () => {
    let tempProducts = products;

    if (category) {
      tempProducts = tempProducts.filter(product => product.categoria.id === category);
    }

    if (brand) {
      tempProducts = tempProducts.filter(product => product.marca.id === brand);
    }

    if (brandName) {
      tempProducts = tempProducts.filter(product => product.nombre.toLowerCase().includes(brandName.toLowerCase()));
    }

    setFilteredProducts(tempProducts);
  };

  const handleAdd=()=>{
    console.log(filterProducts)
  }
  // Los manejadores de cambio actualizan los estados y dependen del filterProducts en el useEffect de arriba
  const handleCategoryChange = event => setCategory(event.target.value);
  const handleBrandChange = event => setBrand(event.target.value);
  const handleBrandNameChange = event => setBrandName(event.target.value);

  if (!isOpen) {
    return null;
  } 
  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <h2>Buscar producto</h2>
        <div className="input-group">
          <label>Categoría</label>
          <select value={category} onChange={handleCategoryChange}>
            <option value="">Seleccione la categoría</option>
            {categories.map((categoria,index) => (
                                        <option key={index} value={categoria.idCategoria}>
                                            {categoria.nombre}
                                        </option>
                                    ))}
          </select>
        </div>
        <div className="input-group">
          <label>Marca</label>
          <select value={brand} onChange={handleBrandChange}>
            <option value="">Seleccione la marca</option>
            {brands.map((marca,index) => (
                                            <option key={index} value={marca.idMarca}>
                                                {marca.nombre}
                                            </option>
                                        ))}
          </select>
        </div>
        <label>Nombre:</label>
        <input 
          type="text" 
          value={brandName} 
          onChange={handleBrandNameChange}
          className="brand-input"
        />
        <div className="modal-buttons">
          <button onClick={onClose} className="cancel-button">Cancelar</button>
          <button onClick={handleAdd} className="add-button">Buscar</button>
        </div>
      </div>
    </div>
  );
};

export default ModalFindProduct;
