import React from 'react';
import './ProductReposition.scss';
import './ProductDetail.scss'; 
import './ProductEdition.scss'; 
import LayoutBasic from '../layouts/LayoutBasic';
import ProductTable from '../components/ProductTable';
const ReposicionProductos = () => {
  // La lógica para manejar la lista de productos, añadir, eliminar, etc. iría aquí.

  return (
    <LayoutBasic>
        <div className="detail-container">
            <div className="detail-header">
                <h1>Reposición de productos</h1>
            </div>
            <ProductTable
                products={this.state.products}
                onRemove={this.handleRemoveProduct}
                />

            <div className="button-bar">
                    <button   className="button-back" >Regresar</button>
                    <button  className="button-edit">Confirmar Reposición</button>
            </div>
           
        </div>
    </LayoutBasic>
    );
};

export default ReposicionProductos;
