import React, { useState } from 'react';
import './ModalAddBrand.scss'; // Asumiendo que tienes un archivo SCSS separado para los estilos
import './ModalFindProduct.scss';

const ModalFindProduct = ({ isOpen, onClose, onAdd,categories,brands,productslist}) => {
  const [brandName, setBrandName] = useState('');
  const [category, setCategory] = useState(categories);
  const [brand, setBrand] = useState(brands);
  const handleBrandNameChange = (event) => {
    setBrandName(event.target.value);
  };

  const [products, setProducts] = useState([]); // Aquí almacenas los productos obtenidos de alguna API o prop
  const [filteredProducts, setFilteredProducts] = useState([]); // Los productos que coinciden con los filtros

  // Suponiendo que recibes los productos como prop y los inicializas así:
  useEffect(() => {
    setProducts(productslist); // Asumiendo que `props.products` contiene todos los productos
    setFilteredProducts(productslist);
  }, [productslist]);

  const handleAddClick = () => {
    onAdd(brandName);
    setBrandName(''); // Resetear el nombre de la marca después de añadir
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <h2>Buscar producto</h2>
        <div className="input-group">
          <label>Categoría</label>
          <select value={category} onChange={e => setCategory(e.target.value)}>
            <option value="">Seleccione la categoría</option>
            {categories.map((categoria,index) => (
                                        <option key={index} value={categoria.idCategoria}>
                                            {categoria.nombre}
                                        </option>
                                    ))}
          </select>
        </div>
        <div className="input-group">
          <label>Marca</label>
          <select value={brand} onChange={e => setBrand(e.target.value)}>
            <option value="">Seleccione la marca</option>
            {brands.map((marca,index) => (
                                            <option key={index} value={marca.idMarca}>
                                                {marca.nombre}
                                            </option>
                                        ))}
          </select>
        </div>
        <label>Nombre:</label>
        <input 
          type="text" 
          value={brandName} 
          onChange={handleBrandNameChange}
          className="brand-input"
        />
        <div className="modal-buttons">
          <button onClick={onClose} className="cancel-button">Cancelar</button>
          <button onClick={handleAddClick} className="add-button">Buscar</button>
        </div>
      </div>
    </div>
  );
};

export default ModalFindProduct;
