import LayoutBasic from "../layouts/LayoutBasic";
import Pagination from "../components/Paginacion";
import Spinner from "../components/Spinner";
import { useNavigate } from 'react-router-dom';
import { useState } from "react";
import './SalesList.scss';
const PRODUCTS_PER_PAGE = 8;
export default function SalesList() {

    const [isLoading, setIsLoading] = useState(false);
    const [sales, setSales] = useState([]);
        
    const [currentPage, setCurrentPage] = useState(1);
    const totalPages = Math.ceil(sales.length / PRODUCTS_PER_PAGE);



     const handlePageChange = (newPage) => {
         setCurrentPage(newPage);
     };

  
  
 
     const indexOfLastProduct = currentPage * PRODUCTS_PER_PAGE;
     const indexOfFirstProduct = indexOfLastProduct - PRODUCTS_PER_PAGE;
     const currentSales = sales.slice(indexOfFirstProduct, indexOfLastProduct);
 
    return (
        <LayoutBasic>
            <div className="sales-container">
                <div className="sales-header">
                    <h1>Ventas realizadas</h1>
                </div>
                {isLoading ? (
                    <Spinner />
                ) : (
                    <div>
                        <div className="sales-filters">
                            

                            <button className="search-btn" >Buscar</button>

                        </div>
                        <div className="sales-table">
                        {sales.length > 0 ? (<div>
                            </div>
        ) : (
          <div className="empty-message">
            <p>No hay ventas realizadas en el d√≠a. <br /> Haz clic en 'Agregar nueva venta' para empezar.</p>
          </div>
        )}
                        </div>
                        <div className="sales-pagination">
                            <Pagination 
                            currentPage={currentPage} 
                            totalPages={totalPages} 
                            onPageChange={handlePageChange} 
                            />
                            <button className="new-btn">Agregar nueva venta</button>
                        </div>
                    </div>
                )}
        </div>
        </LayoutBasic>
    );
}
