import React from 'react';
import PropTypes from 'prop-types';
import checkIcon from '../images/success.png';
import './ModalRegistro.scss'
import  { useEffect } from 'react';

const ModalRegistro = ({ title, buttonText, onConfirm }) => {

    useEffect(() => {
        const timer = setTimeout(() => {
          onConfirm(); // Esto cerrará el modal después de 5 segundos
        }, 5000);
    
        // Asegúrate de limpiar el temporizador si el componente se desmonta antes de que el tiempo expire
        return () => clearTimeout(timer);
      }, [onConfirm]);
    return (
      <div className="modal-overlay">
        <div className="modal-container">
        <div className="timer-bar"></div> {/* Esta es la barra de temporizador que se animará */}
          <div className="modal-content">
            <img src={checkIcon} alt="Confirmación" className="modal-check-icon" />
            <div className="modal-text-content">
              <h2>{title}</h2>
              <button onClick={onConfirm} className="modal-confirm-button">{buttonText}</button>
            </div>
          </div>
        </div>
      </div>
    );
  };

ModalRegistro.propTypes = {
  title: PropTypes.string.isRequired,
  buttonText: PropTypes.string.isRequired,
  onConfirm: PropTypes.func.isRequired
};

export default ModalRegistro;
