import React, { useState } from 'react';
import './ProductReposition.scss';
import LayoutBasic from '../layouts/LayoutBasic';
import ProductTable from '../components/ProductTable';
import addIcon from '../images/add.svg'; // Asegúrate de que este sea un icono de '+'
import { ReactComponent as AddIcon } from '../images/add.svg';
import ModalFindProduct from '../components/ModalFindProudct';
import { useLocation } from 'react-router-dom';

const ReposicionProductos = () => {
  const location = useLocation();
  const { products, categories, brands } = location.state || { products: [], categories: [], brands: [] };
  const [productsNew, setProductsnew] = useState([]);
  const [productslist,setProductslist]= useState(products)
  const handleRemoveProduct = (productId) => {
    setProductsnew(products.filter(product => product.id !== productId));
  };
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddProductModal = () => {
    setIsModalOpen(false);
  };
  return (
    <LayoutBasic>
      <div className="detail-container">
        <div className="detail-header">
          <h1>Reposición de productos</h1>
          <button className="add-product-btn"  onClick={() => setIsModalOpen(true)}>
            <AddIcon className="add-icon" />
            Añadir producto
          </button>
        </div>
        {productsNew.length > 0 ? (
          <ProductTable products={productsNew} onRemove={handleRemoveProduct} />
        ) : (
          <div className="empty-message">
            <p>No hay productos listados. <br /> Haz clic en 'Añadir producto' para empezar.</p>
          </div>
        )}
        <div className="button-bar">
          <button className="button-back" onClick={() => {/* lógica para regresar */}}>
            Regresar
          </button>
          <button className="button-edit" onClick={() => {/* lógica para confirmar reposición */}}>
            Confirmar Reposición
          </button>
        </div>
        {
                  <ModalFindProduct
                  isOpen={isModalOpen}
                  onClose={() => setIsModalOpen(false)}
                  onAdd={handleAddProductModal}
                  categories={categories}
                  brands={brands}
                  productslist={products}
                />
        }
      </div>
    </LayoutBasic>
  );
};

export default ReposicionProductos;
