import React from 'react';
import './ProductDetail.scss'; 
import './ProductEdition.scss';
import LayoutBasic from '../layouts/LayoutBasic';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useState ,useEffect} from 'react';
import ModalRegistro from '../components/ModalRegistro';
import ModalAdvert from '../components/ModalAdvert';
import { updateProduct } from '../api/productos';
import { listarCategorias } from "../api/categoria";
import { listarMarcas } from "../api/marca";
import { listarUnidades } from '../api/unidad';
const ProductAdd = () => {
    const navigate = useNavigate();

    const handleBack = () => {
        navigate(-1); 
    };
    const [productoNuevo, setProductoNuevo] = useState({
        nombre:"",
        stock:0,
        stockMinimo:0,
        precioCompra:0.0,
        precioVenta:0.0,
        idMarca:0,
        idUnidad:0,
        idCategoria:0
    });
    
    const handleNombreChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            nombre: event.target.value
        }));
    };
    const handleStockChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            stock: event.target.value
        }));
    };
    const handleCompraChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            precioCompra: event.target.value
        }));
    };
    const handleVentaChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            precioVenta: event.target.value
        }));
    };
    const handleMinimoChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            stockMinimo: event.target.value
        }));
    };
    const [isConfirmationModalVisible, setIsConfirmationModalVisible] = useState(false);
    const [isRegistrationModalVisible, setIsRegistrationModalVisible] = useState(false);

    const handleGuardarClick = () => {
        setIsConfirmationModalVisible(true);
    };
    // function prepareProductForUpdate(product) {
    //     const { marca, categoria, unidad, ...productToUpdate } = product;
    //     return productToUpdate;
    // }
    // function convertValues(productData) {
    //     // Verifica y convierte stock si es necesario
    //     if (typeof productData.stock === 'string') {
    //         productData.stock = parseInt(productData.stock, 10);
    //     }
    
    //     // Verifica y convierte stockMinimo si es necesario
    //     if (typeof productData.stockMinimo === 'string') {
    //         productData.stockMinimo = parseInt(productData.stockMinimo, 10);
    //     }
    
    //     // Verifica y convierte precioVenta si es necesario
    //     if (typeof productData.precioVenta === 'string') {
    //         productData.precioVenta = parseFloat(productData.precioVenta);
    //     }
    
    //     // Verifica y convierte precioCompra si es necesario
    //     if (typeof productData.precioCompra === 'string') {
    //         productData.precioCompra = parseFloat(productData.precioCompra);
    //     }
    
    //     return productData;
    // }

    const handleConfirmarModal = () => {

    };
    
    

    const handleCancelarModal = () => {
        // Esconder ambos modales
        setIsConfirmationModalVisible(false);
        setIsRegistrationModalVisible(false);
    };

    const handleRegistroConfirmado = () => {
        // Manejar lo que sucede después de confirmar el registro
        setIsRegistrationModalVisible(false);
        navigate("/");
        // Aquí podrías navegar de vuelta a la lista de productos o realizar otra acción
    };
    const handleCategoryChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            idCategoria: event.target.value
        }));
    };
    const handleBrandChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            idMarca: event.target.value
        }));
    };
    const handleUnitChange = (event) => {
        setProductoNuevo(prevProducto => ({
            ...prevProducto,
            idUnidad: event.target.value
        }));
    };
    const [categorias, setCategorias] = useState([]);
    const [marcas, setMarcas] = useState([]);
    const [unidades,setUnidades]=useState([]);
    useEffect(() => {
        listarCategorias().then(data => setCategorias(data));
        listarMarcas().then(data => setMarcas(data));
        listarUnidades().then(data=> setUnidades(data));
    }, []);
    return (
        <LayoutBasic>
            <div className="detail-container">
                <div className="detail-header">
                    <h1>Nuevo producto</h1>
                </div>
                <div className="header-box">
                    Datos Generales del producto
                </div>
                <div className="product-general-info">
                    <div className="info-row">
                        <div className="info-column">
                            <div className="info-label">Nombre de producto:</div>
                            <input
                                type="text"
                                className="info-textbox"
                                value={''}
                                onChange={handleNombreChange}
                            />
                        </div>
                        <div className="info-column">
                            <div className="info-label">Marca:</div>
                            <div className="select-with-button">
                                <select id="brand" name="brand" onChange={handleBrandChange}>
                                    <option value="" selected>Seleccione la marca</option>
                                    {marcas.map((marca, index) => (
                                        <option key={index} value={marca.idMarca}>
                                            {marca.nombre}
                                        </option>
                                    ))}
                                </select>
                                <button className="add-button">Añadir marca</button>
                            </div>
                        </div>

                    </div>
                    <div className="info-row">
                        <div className="info-column">
                        <div className="info-label">Categoría:</div>
                        <div className="info-value">                               
                            <select id="category" name="category"  onChange={handleCategoryChange}>
                                    <option value=""selected>Seleccione la categoría</option>
                                    {categorias.map((categoria,index) => (
                                        <option key={index} value={categoria.idCategoria}>
                                            {categoria.nombre}
                                        </option>
                                    ))}
                            </select></div>
                        </div>
                        <div className="info-column">
                        <div className="info-label">Unidad:</div>
                        <div className="info-value">                            
                            <select id="unit" name="unit"  onChange={handleUnitChange}>
                                <option value=""  selected>Seleccione la unidad</option>
                                    {unidades.map((unidad,index) => (
                                        <option key={index} value={unidad.idUnidad}>
                                            {unidad.nombre}
                                        </option>
                                ))}
                            </select></div>
                        </div>
                    </div>
                </div>
                <div className="header-box">
                    Datos de inventario
                </div>
                <div className="product-general-info">
                    <div className="info-row">
                        <div className="info-column">
                        <div className="info-label">Stock:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={productoNuevo.stock}
                                onChange={handleStockChange}
                            />
                        </div>
                        <div className="info-column">
                        <div className="info-label">Precio compra (S/.) :</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={''}
                                onChange={handleCompraChange}
                            />
                        </div>
                    </div>
                    <div className="info-row">
                        <div className="info-column">
                        <div className="info-label">Precio Venta (S/.) :</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={''}
                                onChange={handleVentaChange}
                            />
                        </div>
                        <div className="info-column">
                        <div className="info-label">Stock minimo:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={''}
                                onChange={handleMinimoChange}
                            />
                        </div>
                    </div>
                </div>
                <div className="button-bar">
                        <button  onClick={handleBack} className="button-back" >Regresar</button>
                        <button onClick={handleGuardarClick} className="button-edit">Guardar producto</button>
                </div>
                {isConfirmationModalVisible && (
                <ModalAdvert
                    bodyText="Estas seguro de querer guardar el detalle de producto?"
                    confirmButtonText="Confirmar"
                    cancelButtonText="Cancelar"
                    onConfirm={handleConfirmarModal}
                    onCancel={handleCancelarModal}
                />
            )}
            {isRegistrationModalVisible && (
                <ModalRegistro
                    title="Producto editado"
                    buttonText="Volver a productos"
                    onConfirm={handleRegistroConfirmado}
                    redirectPath="/"
                />
            )}
            </div>
    </LayoutBasic>
    );
};

export default ProductAdd;
