import React, { useState } from 'react';
import './ProductReposition.scss';
import LayoutBasic from '../layouts/LayoutBasic';
import ProductTable from '../components/ProductTable';

const ReposicionProductos = () => {
  // Suponiendo que tengas un estado inicial para tus productos
  const [products, setProducts] = useState([
    // ... aquí irían los productos iniciales
  ]);

  // Función para manejar la eliminación de un producto
  const handleRemoveProduct = (productId) => {
    // Filtrar el producto que no se quiere eliminar
    const updatedProducts = products.filter(product => product.id !== productId);
    // Actualizar el estado con los productos filtrados
    setProducts(updatedProducts);
  };

  return (
    <LayoutBasic>
        <div className="detail-container">
            <div className="detail-header">
                <h1>Reposición de productos</h1>
            </div>
            <ProductTable
                products={products}
                onRemove={handleRemoveProduct}
            />
            <div className="button-bar">
                <button className="button-back" onClick={() => {/* aquí lógica para regresar */}}>
                  Regresar
                </button>
                <button className="button-edit" onClick={() => {/* aquí lógica para confirmar reposición */}}>
                  Confirmar Reposición
                </button>
            </div>
        </div>
    </LayoutBasic>
  );
};

export default ReposicionProductos;
