import React, { useState } from 'react';
import './ProductReposition.scss';
import LayoutBasic from '../layouts/LayoutBasic';
import ProductTable from '../components/ProductTable';
import addIcon from '../images/add.svg'; // Asegúrate de que este sea un icono de '+'
import { ReactComponent as AddIcon } from '../images/add.svg';

// Luego puedes usarlo en tu JSX como un componente:
<AddIcon className="add-icon" />

const ReposicionProductos = () => {
  const [products, setProducts] = useState([]);

  const handleRemoveProduct = (productId) => {
    setProducts(products.filter(product => product.id !== productId));
  };

  return (
    <LayoutBasic>
      <div className="detail-container">
        <div className="detail-header">
          <h1>Reposición de productos</h1>
          <button className="add-product-btn" onClick={() => {/* lógica para añadir productos */}}>
            <AddIcon className="add-icon" />
            Añadir producto
          </button>
        </div>
        {products.length > 0 ? (
          <ProductTable products={products} onRemove={handleRemoveProduct} />
        ) : (
          <div className="empty-message">
            <p>No hay productos listados. <br /> Haz clic en 'Añadir producto' para empezar.</p>
          </div>
        )}
        <div className="button-bar">
          <button className="button-back" onClick={() => {/* lógica para regresar */}}>
            Regresar
          </button>
          <button className="button-edit" onClick={() => {/* lógica para confirmar reposición */}}>
            Confirmar Reposición
          </button>
        </div>
      </div>
    </LayoutBasic>
  );
};

export default ReposicionProductos;
