import LayoutBasic from "../layouts/LayoutBasic";
import './Products.scss';
import React, { useState,useEffect  } from 'react';
import TableProducts from "../components/TableProduct";
import Pagination from "../components/Paginacion";
import { listarCategorias } from "../api/categoria";
import { listarMarcas } from "../api/marca";
import { listarProductosInventario } from "../api/productos";
// const products = [
//     { id: 1, name: 'Leche tarro azul', brand: 'Gloria' },
//     { id: 2, name: 'Pan de Molde mediano', brand: 'Bimbo' },
//     { id: 3, name: 'Coca Cola 500mL', brand: 'Coca Cola' },
//     { id: 4, name: 'Agua 500 mL', brand: 'San Luis' },
//     { id: 5, name: 'Papel Higiénico 6 rollos', brand: 'Suave' },
//     { id: 6, name: 'Arroz a granel', brand: 'Paisana' },
//     { id: 7, name: 'Lentejas a granel', brand: 'Otro' },
//     { id: 8, name: 'Papa por kilo', brand: 'Otro' },
//     { id: 9, name: 'Leche tarro azul', brand: 'Gloria' },
//     { id: 10, name: 'Pan de Molde mediano', brand: 'Bimbo' },
//     { id: 11, name: 'Coca Cola 500mL', brand: 'Coca Cola' },
//     { id: 12, name: 'Agua 500 mL', brand: 'San Luis' },
//     { id: 13, name: 'Papel Higiénico 6 rollos', brand: 'Suave' },
//     { id: 14, name: 'Arroz a granel', brand: 'Paisana' },
//     { id: 15, name: 'Lentejas a granel', brand: 'Otro' },
//     { id: 16, name: 'Papa por kilo', brand: 'Otro' }
// ];

const PRODUCTS_PER_PAGE = 8;
export default function Products() {

 
     const [categorias, setCategorias] = useState([]);

     useEffect(() => {
        listarCategorias().then(data => {
            if (data && !data.errMsg) {
                setCategorias(data);
            } else {
                console.error(data.errMsg || "Error al cargar categorías");
            }
        });
    }, []);
    const [marcas, setMarcas] = useState([]);

    useEffect(() => {
       listarMarcas().then(data => {
           if (data && !data.errMsg) {
                setMarcas(data);
           } else {
               console.error(data.errMsg || "Error al cargar marcas");
           }
       });
   }, []);
    
   const [products, setProducts] = useState([]);
   useEffect(() => {
        listarProductosInventario().then(data => {
            if (data && !data.errMsg) {
                console.log(data)
                setProducts(data);
            } else {
                console.error(data.errMsg || "Error al cargar productos");
            }
        });
}, []);
     // Estados para la paginación
     const [currentPage, setCurrentPage] = useState(1);
     const totalPages = Math.ceil(products.length / PRODUCTS_PER_PAGE);
 
     // Cambiar página
     const handlePageChange = (newPage) => {
         setCurrentPage(newPage);
     };
 
     // Calcular el rango de productos para la página actual
     const indexOfLastProduct = currentPage * PRODUCTS_PER_PAGE;
     const indexOfFirstProduct = indexOfLastProduct - PRODUCTS_PER_PAGE;
     const currentProducts = products.slice(indexOfFirstProduct, indexOfLastProduct);

    return (
        <LayoutBasic>
            <div className="products-container">
                <div className="products-header">
                    <h1>Inventario - Productos</h1>
                    <button className="add-merchandise-btn">Añadir mercadería</button>
                </div>
                <div className="products-filters">
                    <div className="filter-item">
                        <label htmlFor="category">Categoría</label>
                        <select id="category" name="category">
                            <option value="" disabled selected>Seleccione la categoría</option>
                            {categorias.map((categoria) => (
                                <option key={categoria.idCategoria} value={categoria.idCategoria}>
                                    {categoria.nombre}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="filter-item">
                        <label htmlFor="brand">Marca</label>
                        <select id="brand" name="brand">
                            <option value="" disabled selected>Seleccione la marca</option>
                                {marcas.map((marca) => (
                                    <option key={marca.idCategoria} value={marca.idCategoria}>
                                        {marca.nombre}
                                    </option>
                                ))}
                        </select>
                        
                    </div>
                    <div className="filter-item">
                        <label htmlFor="name">Nombre</label>
                        <input type="text" id="name" name="name" />
                    </div>
                    <div className="filter-item checkbox">
                        <input type="checkbox" id="lowStock" name="lowStock" />
                        <label htmlFor="lowStock">Stock bajo</label>
                    </div>
                    <button className="search-btn">Buscar</button>
                </div>
                <div className="products-table">
                    <TableProducts products = {currentProducts}/>
                </div>
                <div className="products-pagination">
                    <Pagination 
                    currentPage={currentPage} 
                    totalPages={totalPages} 
                    onPageChange={handlePageChange} 
                     />
                </div>
            </div>
        </LayoutBasic>
    );
}
