import React, { useState } from 'react';
import './ModalAddBrand.scss'; // Asumiendo que tienes un archivo SCSS separado para los estilos
import './ModalFindProduct.scss';
import ModalProductSelect from './ModalProductSelect'; // Asegúrate de que la ruta es correcta

import { useEffect } from 'react';
const ModalFindProduct = ({ isOpen, onClose, onAdd, categories, brands, productslist }) => {
  const [brandName, setBrandName] = useState('');
  const [category, setCategory] = useState('');
  const [brand, setBrand] = useState('');
  const [filteredProducts, setFilteredProducts] = useState(productslist);
  const [isProductSelectModalOpen, setIsProductSelectModalOpen] = useState(false);

  

  const filterProducts = () => {
    let tempProducts = filteredProducts;
    console.log(tempProducts)
    if (category) {
      tempProducts = tempProducts.filter(product => String(product.categoria.idCategoria) === category);
    }

    if (brand) {
      tempProducts = tempProducts.filter(product => String(product.marca.idMarca) === brand);
    }

    if (brandName) {
      tempProducts = tempProducts.filter(product => product.nombre.toLowerCase().includes(brandName.toLowerCase()));
    }

    setFilteredProducts(tempProducts);
  };
  const handleAdd = () => {
    filterProducts(); // Filtra los productos antes de hacer el console.log
    console.log(filteredProducts);
    setIsProductSelectModalOpen(true); // Abre el nuevo modal
  };
  const closeProductSelectModal = () => {
    setIsProductSelectModalOpen(false);
  };
  

  const handleCategoryChange = event => {
    setCategory(event.target.value);
  };

  const handleBrandChange = event => {
    setBrand(event.target.value);
  };

  const handleBrandNameChange = event => {
    setBrandName(event.target.value);
  };

  if (!isOpen) {
    return null;
  } 
  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <h2>Buscar producto</h2>
        <div className="input-group">
          <label>Categoría</label>
          <select value={category} onChange={handleCategoryChange}>
            <option value="">Seleccione la categoría</option>
            {categories.map((categoria,index) => (
                                        <option key={index} value={categoria.idCategoria}>
                                            {categoria.nombre}
                                        </option>
                                    ))}
          </select>
        </div>
        <div className="input-group">
          <label>Marca</label>
          <select value={brand} onChange={handleBrandChange}>
            <option value="">Seleccione la marca</option>
            {brands.map((marca,index) => (
                                            <option key={index} value={marca.idMarca}>
                                                {marca.nombre}
                                            </option>
                                        ))}
          </select>
        </div>
        <label>Nombre:</label>
        <input 
          type="text" 
          value={brandName} 
          onChange={handleBrandNameChange}
          className="brand-input"
        />
        <div className="modal-buttons">
          <button onClick={onClose} className="cancel-button">Cancelar</button>
          <button onClick={handleAdd} className="add-button">Buscar</button>
        </div>
      </div>
      {isProductSelectModalOpen && (
      <ModalProductSelect
        isOpen={isProductSelectModalOpen}
        onClose={closeProductSelectModal}
        products={filteredProducts}
        onProductSelect={(selectedProduct) => {
          console.log('Producto seleccionado:', selectedProduct);
          closeProductSelectModal(); // Opcional: cerrar modal tras seleccionar producto
        }}
      />
    )}
    </div>
  );
};

export default ModalFindProduct;
