import React, { useState,useEffect } from 'react';
import LayoutBasic from '../layouts/LayoutBasic';
import { Navigate,useNavigate } from 'react-router-dom';
import './SalesNew.scss'
import TableNewSale from '../components/TableNewSale';
import { useLocation } from 'react-router-dom';

const SalesDetail =() => {
    const location = useLocation();
    const {clientPrevio, montoTotalPrevio } = location.state;
    const [products,setProducts]=useState([]);
    const navigate=useNavigate();
    const [montoTotal,setMontoTotal]=useState(montoTotalPrevio)
    const [cliente,setCliente]=useState(clientPrevio)

    // useEffect(() => {
    //   listarCategorias().then(data => setCategorias(data));
    //   listarMarcas().then(data => setMarcas(data));
    //   listarProductosInventario().then(data => {
    //     const productosActivos = data.filter(producto => producto.estado === 1);
    //     const productosConCantidad = productosActivos.map(producto => {
    //       return {...producto, cantidad: 0}; // Agrega la propiedad cantidad inicializada en 0
    //     });
    //     setProducts(productosConCantidad);
    //   });
    // }, []);
    
    return (
        <LayoutBasic>
          <div className="sale-container">
            <div className="sale-header">
              <h1>Venta</h1>
            </div>
            <div className="header-box">
                    Datos Generales del cliente
            </div>
            <div className='client-general-info'>
                <div className='info-row'>
                    <div className="info-column">
                        <div className="info-label">Nombre:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={cliente.nombre}
                        />
                    </div>
                    <div className="info-column">
                        <div className="info-label">Número de DNI:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={cliente.dni}
                        />
                    </div>
                    <div className="info-column">
                        <div className="info-label">Número de teléfono:</div>
                        <input
                                disabled="true"
                                type="text"
                                className="info-textbox"
                                value={cliente.celular}
                        />
                    </div>
                </div>
            </div>
            <div className="table-container-sale">
            {products.length > 0 ? (
                <TableNewSale products={products}/>
            ) : (
              <div className="empty-message">
                <p>No hay productos listados</p>
              </div>
            )}
            </div>
            <div className="button-bar">
              <button className="button-back" onClick={() => navigate(-1)}>
                Regresar
              </button>
              <div className='saleConfirm'>
                <h2>Monto Total (S/.) {montoTotal}</h2>

              </div>

            </div>
        
          </div>
        
        </LayoutBasic>
      );
};
export default SalesDetail;
