import LayoutBasic from "../layouts/LayoutBasic";
import './ProductMovement.scss';
import React, { useState ,useEffect} from 'react';
import Pagination from "../components/Paginacion";
import TableMovements from "../components/TableMovements";
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { listarMovimientosProducto } from "../api/productos";
import Spinner from "../components/Spinner";

// const movements = [
//     { id: 1, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 2, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 3, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 4, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 5, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 6, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 7, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 8, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 9, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 10, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 11, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 12, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 13, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 14, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 15, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },
//     { id: 16, fecha: '11/04/2024', hora: '11:25:30',tipo:0,cantidad:-1 },

// ];



const PRODUCTS_PER_PAGE = 8;
export default function ProductMovement() {
    const location = useLocation();
    const { product } = location.state || {};
    const [productInfo,setProductInfo]=useState(product);
    const [movements,setMovements]=useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        listarMovimientosProducto(productInfo.idProducto).then(data => {
            const movimientosProcesados = data.map(movimiento => {
                const fechaHora = new Date(movimiento.fechaMovimiento);
                return {
                    ...movimiento,
                    fecha: fechaHora.toLocaleDateString(), // 'MM/dd/yyyy' por ejemplo
                    hora: fechaHora.toLocaleTimeString() // 'HH:mm:ss' por ejemplo
                };
            });
            setMovements(movimientosProcesados);
            setIsLoading(false);
        });
    }, [productInfo.idProducto]);
    
     const [currentPage, setCurrentPage] = useState(1);
     const totalPages = Math.ceil(movements.length / PRODUCTS_PER_PAGE);
 
     const handlePageChange = (newPage) => {
         setCurrentPage(newPage);
     };
 
     const indexOfLastProduct = currentPage * PRODUCTS_PER_PAGE;
     const indexOfFirstProduct = indexOfLastProduct - PRODUCTS_PER_PAGE;
     const currentMovements = movements.length ? movements.slice(indexOfFirstProduct, indexOfLastProduct) : [];
     
     const navigate = useNavigate();

     const handleBack = () => {
        navigate(-1);
    };
    return (
        <LayoutBasic>
            <div className="products-container2">
                <div className="products-header2">
                    <h1>Movimientos de inventario-{productInfo.nombre} {productInfo.marca.nombre}</h1>
                    <h2></h2>
                </div>
                {isLoading ? (
                    <Spinner />
                ) : (<div>
                <div className="products-filters2">
                    <h2>Búsqueda</h2>
                    <div className="filter-item">
                        <label htmlFor="startDate">Fecha Inicio</label>
                        <input type="date" id="startDate" name="startDate" />
                    </div>
                    <div className="filter-item">
                        <label htmlFor="endDate">Fecha Fin</label>
                        <input type="date" id="endDate" name="endDate" />
                    </div>
                    <div className="filter-item">
                        <label htmlFor="movementType">Tipo de movimiento</label>
                        <select id="movementType" name="movementType">
                            <option value="">Seleccione el tipo</option>
                            {/* Opciones de tipos de movimiento aquí */}
                        </select>
                    </div>
                    <button className="search-btn">Buscar</button>
                </div>
                <div className="products-table">
                    <TableMovements movements = {currentMovements}/>
                </div>
                <div className="products-pagination-and-back2">
                    <Pagination 
                        currentPage={currentPage} 
                        totalPages={totalPages} 
                        onPageChange={handlePageChange} 
                    />
                    <button onClick={handleBack} className="back-btn">Regresar</button>
                    </div>
            </div>)}
            </div>
        </LayoutBasic>
    );
}
