import{BASE_PATH} from './config';

export function listarProductosInventario() {
    const url = `${BASE_PATH}api/products`;
    console.log("URL para la solicitud listar:", url);  // Esto te ayudará a verificar la URL

    const params = {
        method: "GET"
    };
    return fetch(url, params)
    .then(response => {
        if (!response.ok) {
            throw new Error('Error en la respuesta del servidor: ' + response.statusText);
        }
        return response.json();
    })
    .then(data => {
        return data;
    })
    .catch(error => {
        console.error("Error al listar categorías: ", error);
        return {
            errMsg: error.message,
            success: false
        };
    });
    
}

export function updateProduct(productId, productData) {
    fetch(`${BASE_PATH}api/products/${productId}`, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(productData)
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        return response.json();
    })
    .then(data => {
        console.log('Producto actualizado con éxito:', data);
    })
    .catch(error => {
        console.error('Error al actualizar el producto:', error);
    });
}
