import React, { useState } from 'react';
import './ProductReposition.scss';
import LayoutBasic from '../layouts/LayoutBasic';
import ProductTable from '../components/ProductTable';
import addIcon from '../images/add.png';

const ReposicionProductos = () => {
  const [products, setProducts] = useState([
    // ... aquí irían los productos iniciales, si los hay
  ]);

  const handleRemoveProduct = (productId) => {
    const updatedProducts = products.filter(product => product.id !== productId);
    setProducts(updatedProducts);
  };

  return (
    <LayoutBasic>
        <div className="detail-container">
            <div className="detail-header">
                <h1>Reposición de productos</h1>
            </div>
            {products.length > 0 ? (
                <ProductTable
                    products={products}
                    onRemove={handleRemoveProduct}
                />
            ) : (
                <div className="empty-message">
                <img src={addIcon} alt="Añadir" />
                <p>Añadir productos</p>
                </div>
            )}
            <div className="button-bar">
                <button className="button-back" onClick={() => {/* lógica para regresar */}}>
                  Regresar
                </button>
                <button className="button-edit" onClick={() => {/* lógica para confirmar reposición */}}>
                  Confirmar Reposición
                </button>
            </div>
        </div>
    </LayoutBasic>
  );
};

export default ReposicionProductos;
