import React, { useState } from 'react';
import './ModalFindProduct.scss'; // Asegúrate de tener un archivo SCSS para estilos

const ModalFindProduct = ({ isOpen, onClose, onAdd }) => {
  const [category, setCategory] = useState('');
  const [brand, setBrand] = useState('');
  const [productName, setProductName] = useState('');

  const handleAddClick = () => {
    // Lógica para añadir producto
    onAdd({ category, brand, productName });
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <div className="modal-busqueda-producto">
        <h2>Añadir producto</h2>
        <div className="input-group">
          <label>Categoría</label>
          <select value={category} onChange={e => setCategory(e.target.value)}>
            <option value="">Seleccione la categoría</option>
            {/* Opciones de categorías */}
          </select>
        </div>
        <div className="input-group">
          <label>Marca</label>
          <select value={brand} onChange={e => setBrand(e.target.value)}>
            <option value="">Seleccione la marca</option>
            {/* Opciones de marcas */}
          </select>
        </div>
        <div className="input-group">
          <label>Nombre:</label>
          <input
            type="text"
            value={productName}
            onChange={e => setProductName(e.target.value)}
            placeholder="Leche gloria caja"
          />
          <button onClick={() => {/* Lógica de búsqueda */}}>Buscar</button>
        </div>
        <div className="modal-buttons">
          <button onClick={onClose}>Cancelar</button>
          <button onClick={handleAddClick}>Añadir</button>
        </div>
      </div>
    </Modal>
  );
};

export default ModalFindProduct;
