import React from 'react';
import './ProductTable.scss'; // Asegúrate de tener este archivo CSS para estilizar tu tabla.

const ProductTable = ({ products, onRemove }) => {
  return (
    <table className="product-table">
      <thead>
        <tr>
          <th></th>
          <th>Nombre de producto</th>
          <th>Unidad</th>
          <th>Marca</th>
          <th>Categoría</th>
          <th>Cantidad</th>
        </tr>
      </thead>
      <tbody>
        {products.map((product, index) => (
          <tr key={index}>
            <td className="remove-cell" onClick={() => onRemove(product.id)}>X</td>
            <td>{product.nombre}</td>
            <td>{product.unidad}</td>
            <td>{product.marca}</td>
            <td>{product.categoria}</td>
            <td>{product.cantidad}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ProductTable;
