import React, { useState } from 'react';
import './ModalAddBrand.scss'; // Asumiendo que tienes un archivo SCSS separado para los estilos
import './ModalFindProduct.scss';

const ModalFindProduct = ({ isOpen, onClose, onAdd,categories,brands }) => {
  const [brandName, setBrandName] = useState('');
  const [category, setCategory] = useState(categories);
  const [brand, setBrand] = useState(brands);
  const handleBrandNameChange = (event) => {
    setBrandName(event.target.value);
  };

  const handleAddClick = () => {
    onAdd(brandName);
    setBrandName(''); // Resetear el nombre de la marca después de añadir
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <h2>Buscar producto</h2>
        <div className="input-group">
          <label>Categoría</label>
          <select value={category} onChange={e => setCategory(e.target.value)}>
            <option value="">Seleccione la categoría</option>
            {/* Opciones de categorías */}
          </select>
        </div>
        <div className="input-group">
          <label>Marca</label>
          <select value={brand} onChange={e => setBrand(e.target.value)}>
            <option value="">Seleccione la marca</option>
            {/* Opciones de marcas */}
          </select>
        </div>
        <label>Nombre:</label>
        <input 
          type="text" 
          value={brandName} 
          onChange={handleBrandNameChange}
          className="brand-input"
        />
        <div className="modal-buttons">
          <button onClick={onCancel} className="cancel-button">Cancelar</button>
          <button onClick={handleAddClick} className="add-button">Añadir</button>
        </div>
      </div>
    </div>
  );
};

export default ModalFindProduct;
