import React, { useState } from 'react';
import LayoutBasic from '../layouts/LayoutBasic';
import { Navigate,useNavigate } from 'react-router-dom';
import './SalesNew.scss'
const SalesNew =() => {
    const [products,setProducts]=useState([]);
    const navigate=useNavigate();
    const [cliente,setCliente]=useState({
        nombre:"",
        dni:"",
        telefono:""
    })
    const handleNombreChange = (event) => {
        setCliente(prevCliente => ({
            ...prevCliente,
            nombre: event.target.value
        }));
    };
    const handleDniChange = (event) => {
        setCliente(prevCliente => ({
            ...prevCliente,
            dni: event.target.value
        }));
    };
    const handleTelefonoChange = (event) => {
        setCliente(prevCliente => ({
            ...prevCliente,
            telefono: event.target.value
        }));
    };
    return (
        <LayoutBasic>
          <div className="sale-container">
            <div className="sale-header">
              <h1>Nueva venta</h1>
            </div>
            <div className="header-box">
                    Datos Generales del cliente
            </div>
            <div className='client-general-info'>
                <div className='info-row'>
                    <div className="info-column">
                        <div className="info-label">Nombre:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={cliente.nombre}
                                onChange={handleNombreChange}
                        />
                    </div>
                    <div className="info-column">
                        <div className="info-label">NÃºmero de DNI:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={cliente.dni}
                                onChange={handleDniChange}
                        />
                    </div>
                    <div className="info-column">
                        <div className="info-label">NÃºmero de telÃ©fono:</div>
                        <input
                                type="text"
                                className="info-textbox"
                                value={cliente.telefono}
                                onChange={handleTelefonoChange}
                        />
                    </div>
                </div>
            </div>
            <div className="header-box">
                    Productos en venta
            </div>
            <div className="table-container-sale">
            {products.length > 0 ? (
                <div></div>
            ) : (
              <div className="empty-message">
                <p>No hay productos listados. <br /> Haz clic en 'AÃ±adir producto' para empezar.</p>
              </div>
            )}
            </div>
            <div className="button-bar">
              <button className="button-back" onClick={() => navigate(-1)}>
                Regresar
              </button>
              <button className="button-edit">
                Confirmar Venta
              </button>
            </div>
        
          </div>
        </LayoutBasic>
      );
};
export default SalesNew;
