import React from 'react';
import PropTypes from 'prop-types';
import checkIcon from './check-icon.svg'; // Asegúrate de que el icono de verificación esté en tu proyecto

const ModalRegistro = ({ title, buttonText, onConfirm }) => {
  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <div className="modal-content">
          <img src={checkIcon} alt="Confirmación" className="modal-check-icon" />
          <h2>{title}</h2>
        </div>
        <div className="modal-footer">
          <button onClick={onConfirm} className="modal-confirm-button">{buttonText}</button>
        </div>
      </div>
    </div>
  );
};

ModalRegistro.propTypes = {
  title: PropTypes.string.isRequired,
  buttonText: PropTypes.string.isRequired,
  onConfirm: PropTypes.func.isRequired
};

export default ModalRegistro;
