import{BASE_PATH} from './config';

export function listarProductosInventario() {
    const url = `${BASE_PATH}api/products`;
    console.log("URL para la solicitud listar:", url);  // Esto te ayudará a verificar la URL

    const params = {
        method: "GET"
    };
    return fetch(url, params)
    .then(response => {
        if (!response.ok) {
            throw new Error('Error en la respuesta del servidor: ' + response.statusText);
        }
        return response.json();
    })
    .then(data => {
        return data;
    })
    .catch(error => {
        console.error("Error al listar categorías: ", error);
        return {
            errMsg: error.message,
            success: false
        };
    });
    
}

// Nueva función para actualizar un producto
export function actualizarProducto(id, productData) {
    const url = `${BASE_PATH}api/products/${id}`;
    console.log("URL para la solicitud PUT:", url);  // Esto te ayudará a verificar la URL

    const params = {
        method: "PUT",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify(productData)
    };

    return fetch(url, params)
        .then(response => {
            if (!response.ok) {
                throw new Error('Error en la respuesta del servidor: ' + response.statusText);
            }
            return response.json();
        })
        .then(data => {
            return {
                data: data,
                success: true
            };
        })
        .catch(error => {
            console.error("Error al actualizar el producto: ", error);
            return {
                errMsg: error.message,
                success: false
            };
        });
}
