import{BASE_PATH} from './config';

export  function listarCategorias(){
    const url = `${BASE_PATH}api/categorias`;
    const params = {
        method: "GET"
    }
    return fetch(url, params)
    .then(response => {
        return response.json()
    })
    .then(data => {
        return data;
    })
    .catch(errMsg => {
        return {
            errMsg: errMsg.message,
            success: false
        }
    })  
}
