import './ProductTable.scss'; // Asegúrate de tener este archivo CSS para estilizar tu tabla.
import React, { useState, useEffect } from 'react';

const ProductTable = ({ products, onRemove, onUpdateQuantity }) => {
  const [quantities, setQuantities] = useState({});

  // Inicializa el estado de las cantidades cuando los productos cambian
  useEffect(() => {
    const initialQuantities = {};
    products.forEach(product => {
      // Inicializa todas las cantidades en cero o como cadena vacía si deseas que el usuario las introduzca
      initialQuantities[product.idCategoria] = ''; // Inicia como cadena vacía
    });
    setQuantities(initialQuantities);
  }, [products]);

  // Maneja el cambio en los inputs de cantidad
  const handleQuantityChange = (id, value) => {
    const newQuantities = { ...quantities, [id]: value };
    setQuantities(newQuantities);
    // Convierte a float o maneja el valor como necesario antes de enviar a una función externa
    if (value && !isNaN(value)) {
      onUpdateQuantity(id, parseFloat(value));
    }
  };

  return (
    <table className="product-table">
      <thead>
        <tr>
          <th></th>
          <th>Nombre de producto</th>
          <th>Marca</th>
          <th>Categoría</th>
          <th>Cantidad</th>
          <th>Unidad</th>
        </tr>
      </thead>
      <tbody>
        {products.map(product => (
          <tr key={product.id}>
            <td className="remove-cell" onClick={() => onRemove(product.id)}>X</td>
            <td>{product.nombre}</td>
            <td>{product.marca.nombre}</td>
            <td>{product.categoria.nombre}</td>
            <td>
              <input
                type="text"
                className="quantity-input"
                value={quantities[product.idProducto] || ''} // Utiliza el valor del estado o vacío si no está definido
                onChange={e => handleQuantityChange(product.idProducto, e.target.value)}
              />
            </td>
            <td>{product.unidad.nombre}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ProductTable;
