import React from 'react';
import './TableMovement.scss';
import { MOVEMENT_TYPES } from '../enum/enum';

const getEstadoPromocion = (promo) => {
    const today = new Date().toISOString().split('T')[0]; // Obtener la fecha de hoy en formato YYYY-MM-DD

    if (promo.estado === 0) {
        return 'Cancelado';
    } else if (promo.estado === 1) {
        if (today >= promo.fechaInicio && today <= promo.fechaFin) {
            return 'Activo';
        } else {
            return 'Creado';
        }
    } else {
        return 'Desconocido'; // Por si acaso hay algún valor no esperado
    }
};

const PromotionsTable = ({ promotions }) => {
    return (
        <div className="products-table">
            <table>
                <thead>
                    <tr>
                        <th>Número</th>
                        <th>Descripcion</th>
                        <th>Tipo de promoción</th>
                        <th>Fecha de inicio</th>
                        <th>Fecha de expiración</th>
                        <th>Estado</th>
                        <th>Detalle</th>
                    </tr>
                </thead>
                <tbody>
                    {promotions.map((promo, index) => (
                        <tr key={promo.idPromocion}>
                            <td>{index + 1}</td>
                            <td>{promo.descripcion}</td>
                            <td>{promo.tipoPromocion}</td>
                            <td>{promo.fechaInicio}</td>
                            <td>{promo.fechaFin}</td>
                            <td>{getEstadoPromocion(promo)}</td>
                            <td><button className="details-btn">Ver detalle</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default PromotionsTable;
