import React, {  useState } from 'react';
import { NavLink } from 'react-router-dom';
import './Navbar.scss';
const admin = [
    {
        title: "Productos",
        link: "/"
        
    },
]

export default function Navbar() {
    const [isOpen, setIsOpen] = useState(false);
    return(
        <div className="Navbar" >
            <div className="Navbar__toggle" onClick={() => setIsOpen(!isOpen)}>
                {/* Aquí puedes usar un ícono en lugar de este texto */}
                <span>☰</span> 
            </div>
            <nav style={{marginTop:"10px", height: "fit-content"}}>
                <ul className="NavBar__sidebarList">
                    {admin.map((val,key)=> {
                        return (
                            <NavLink key = {key}
                                to = {val.link}
                                className = {e => 
                                    `NavBar__sidebarList-dataRow ${e.isActive? "selected" : ""}`
                                  }
                                >
                               
                                <span className='NavBar__sidebarList-texto'>
                                    {val.title}
                                </span>
                            </NavLink>
                        )
                    })}
                </ul>
            </nav>
        </div>
    )
    
}
